function AircraftSystem()local a={}local b=1;local c={min=0.5,max=0.75}local d={plane={x=0,y=56,xw=16,yw=16},jet={x=16,y=56,xw=16,yw=16}}local e={top=0,right=128,bottom=16,left=0}local f=4;local g=0;local h={}function round_robin_distribution()g=(g+1)%f;return g end;function craft(i)local craft={}i=i or false;local j={}local k={x=0,y=0}local l=rnd(c.max-c.min)+c.min;local m=flr(rnd(128))if i then local n=round_robin_distribution()local o=rnd(e.right/f)+e.right/f*n;local p=rnd(e.bottom)k={x=o,y=p}else local o=e.right+10;local p=rnd(e.top+e.bottom)+e.top;k={x=o,y=p}end;function craft:draw()local o=flr(k.x)local p=flr(k.y)local q=d.plane;if p%2==0 then q=d.jet end;sspr(q.x,q.y,q.xw,q.yw,o,p)end;function craft:update()k.x=k.x-l;if k.x<e.left-128-m then k.x=e.right+rnd(5)+5;k.y=rnd(e.bottom)end end;return craft end;function init()for r=1,b do add(h,craft(true))end end;function a:draw()for craft in all(h)do craft.draw()end end;function a:update()for craft in all(h)do craft.update()end end;init()return a end