function GameOver(a)local b=0;local b=0;local c=90;local d=false;function game_over_controller()if not d then d=true;music(0)end;b=b+1;if b>c then b=0;a.screen=TitleScreen(a)end end;function game_over_view()cls()if a.score>a.high_score then multi_tone_print("you set the new high score!",10,40,{{color=8,height=1},{color=9,height=1},{color=10,height=1},{color=11,height=1},{color=12,height=1}})printc(""..a.score.."",15,55,7)printc("play again and beat it!",15,70,7)elseif a.score>0 then printc("game over - try again!",20,60,7)printc("your score: "..a.score.." great job!",15,80,7)else printc("game over - try again!",20,60,7)end end;return{view=game_over_view,controller=game_over_controller}end