function Credits(a)local b=128;local c=.50;local d=0;local e=5;local f=1;local g={developers={header="developers",entities={'jon','michael','francis'}},children={header="children",entities={'riker','lily','lizzie','maverick','lukas'}},betaTesters={header="beta testers",entities={'imadabes','kenny','sara','amanda','berekley','maverick','gavin','lukas','lily','riker','lizzie'}},specialThanks={header='special thanks',entities={'level 15','ihop','the love house','the mommies','pico-8','uncrustables'}}}local h={8,9,10,11,12,13,14}local i=0;for j,k in pairs(g)do i=i+10;i=i+#k.entities*10+10 end;function credits_controller()pause_suppress()b=b-c;if b<-i or btnp(6)then a.screen=screens.title end;d=d+1;if d>e then d=0;f=f+1;if f>#h then f=1 end end end;function credits_view()cls()local l=b;for j,k in pairs(g)do printc(k.header,x,l,h[f])l=l+10;for j,m in ipairs(k.entities)do printc(m,x,l,7)l=l+10 end;l=l+10 end end;return{controller=credits_controller,view=credits_view}end