function CloudSystem()local a={}local b=6;local c={min=0.025,max=0.1}local d={top=0,right=128,bottom=50,left=0}local e=4;local f=0;local g={}function round_robin_distribution()f=(f+1)%e;return f end;function Cloud(h)local i={}h=h or false;local j=flr(rnd(3))+66;local k={x=0,y=0}local l=rnd(c.max-c.min)+c.min;if h then local m=round_robin_distribution()local n=rnd(d.right/e)+d.right/e*m;local o=rnd(d.bottom)k={x=n,y=o}else local n=d.right+10;local o=rnd(d.top+d.bottom)+d.top;k={x=n,y=o}end;function i:draw()local n=flr(k.x)local o=flr(k.y)spr(j,n,o)end;function i:update()k.x=k.x-l;if k.x<d.left-10 then k.x=d.right+rnd(5)+5;k.y=rnd(d.bottom)end end;return i end;function init()for p=1,b do add(g,Cloud(true))end end;function a:draw()for i in all(g)do i.draw()end end;function a:update()for i in all(g)do i.update()end end;init()return a end