function Info(a)cls()scroll_y=128;local scroll_y=128;local b=1;local c={{name="jump over these obstacles!",sprite=-1},{name="poop",sprite=192},{name="rock",sprite=193},{name="bush",sprite=194},{name="traffic cone",sprite=195},{name="mailbox",sprite=196},{name="trash can",sprite=197},{name="fire hydrant",sprite=198},{name="balloon",sprite=199},{name="no-crossing sign",sprite=200},{name="yield sign",sprite=201},{name="puddle",sprite=202},{name="fence",sprite=203},{name="good luck - you can do it!",sprite=-1}}function view()for d=1,#c do local e=c[d]local f=scroll_y+(d-1)*48;if f>-16 and f<128 then local g=64-4;spr(e.sprite,g,f)local h=64-#e.name*4/2;local i=f+12;print(e.name,h,i,7)end end end;function controller()pause_suppress()if btnp(6)then a.screen=screens.title end;scroll_y=scroll_y-b;if scroll_y<-#c*48 then scroll_y=128 end end;return{view=view,controller=controller}end